mapper.select:
select
/* ${random} */
from t_plugin_info tpi where tpi.plugin_id in (select plugin_id from t_auth_feature_info tafi where tafi.dev_id = #{devId,jdbcType=VARCHAR}) and tpi.bind_userphone = #{phoneNum,jdbcType=VARCHAR} order by tpi.start_time desc limit 1

select
/* ${random} */
plugin_key from t_plugin_info where plugin_id = #{pluginId,jdbcType=VARCHAR} and status = #{status,jdbcType=INTEGER}

mapper.update:
update t_plugin_info tpi
set tpi.status = #{status,jdbcType=INTEGER}
where tpi.plugin_id in (
select tafi.plugin_id
from t_auth_feature_info tafi
where tafi.dev_type = #{devTpye,jdbcType=VARCHAR}

    and tafi.uuid = #{uuid,jdbcType=VARCHAR}


    and tafi.imei = #{imei,jdbcType=VARCHAR}

) and tpi.status < #{status,jdbcType=INTEGER}
and tpi.bind_userphone = #{bindUserphone,jdbcType=VARCHAR}
and tpi.app_code = #{appCode,jdbcType=VARCHAR}
